INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000166', 'nucleotide binding', '"Interacting selectively and non-covalently with a nucleotide, any compound consisting of a nucleoside that is esterified with (ortho)phosphate or an oligophosphate at any hydroxyl group on the ribose or deoxyribose." [GOC:mah, ISBN:0198547684]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000287', 'magnesium ion binding', '"Interacting selectively and non-covalently with magnesium (Mg) ions." [GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000975', 'regulatory region DNA binding', '"Interacting selectively and non-covalently with a DNA region that regulates a DNA-based process. Such processes include transcription, DNA replication, and DNA repair." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000976', 'transcription regulatory region sequence-specific DNA binding', '"Interacting selectively and non-covalently with a specific sequence of DNA that is part of a regulatory region that controls transcription of that section of the DNA. The transcribed region might be described as a gene, cistron, or operon." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000977', 'RNA polymerase II regulatory region sequence-specific DNA binding', '"Interacting selectively and non-covalently with a specific sequence of DNA that is part of a regulatory region that controls the transcription of a gene or cistron by RNA polymerase II." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000978', 'RNA polymerase II core promoter proximal region sequence-specific DNA binding', '"Interacting selectively and non-covalently with a sequence of DNA that is in cis with and relatively close to a core promoter for RNA polymerase II." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000981', 'RNA polymerase II transcription factor activity, sequence-specific DNA binding', '"Interacting selectively and non-covalently with a specific DNA sequence in order to modulate transcription by RNA polymerase II. The transcription factor may or may not also interact selectively with a protein or macromolecular complex." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000982', 'transcription factor activity, RNA polymerase II core promoter proximal region sequence-specific binding', '"Interacting selectively and non-covalently with a sequence of DNA that is in cis with and relatively close to a core promoter for RNA polymerase II (RNAP II) in order to modulate transcription by RNAP II." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000987', 'core promoter proximal region sequence-specific DNA binding', '"Interacting selectively and non-covalently with a sequence of DNA that is in cis with and relatively close to the core promoter. The transcribed region might be described as a gene, cistron, or operon." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000988', 'transcription factor activity, protein binding', '"Interacting selectively and non-covalently with any protein or protein complex (a complex of two or more proteins that may include other nonprotein molecules), in order to modulate transcription. A protein binding transcription factor may or may not also interact with the template nucleic acid (either DNA or RNA) as well." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0000989', 'transcription factor activity, transcription factor binding', '"Interacting selectively and non-covalently with a specific transcription factor, which may be a single protein or a complex, in order to modulate transcription. A protein binding transcription factor may or may not also interact with the template nucleic acid (either DNA or RNA) as well." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0001012', 'RNA polymerase II regulatory region DNA binding', '"Interacting selectively and non-covalently with a DNA region that controls the transcription of a region of DNA by RNA polymerase II. Binding may occur as a sequence specific interaction or as an interaction observed only once a factor has been recruited to the DNA by other factors." [GOC:txnOH, GOC:vw]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0001067', 'regulatory region nucleic acid binding', '"Interacting selectively and non-covalently with a nucleic acid region that regulates a nucleic acid-based process. Such processes include transcription, DNA replication, and DNA repair." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0001071', 'nucleic acid binding transcription factor activity', '"Interacting selectively and non-covalently with a DNA or RNA sequence in order to modulate transcription. The transcription factor may or may not also interact selectively with a protein or macromolecular complex." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0001077', 'transcriptional activator activity, RNA polymerase II core promoter proximal region sequence-specific binding', '"Interacting selectively and non-covalently with a sequence of DNA that is in cis with and relatively close to a core promoter for RNA polymerase II (RNAP II) in order to activate or increase the frequency, rate or extent of transcription from the RNAP II promoter." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0001159', 'core promoter proximal region DNA binding', '"Interacting selectively and non-covalently with a region of DNA that is in cis with and relatively close to the core promoter. The transcribed region might be described as a gene, cistron, or operon." [GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0001228', 'transcriptional activator activity, RNA polymerase II transcription regulatory region sequence-specific binding', '"Interacting selectively and non-covalently with a sequence of DNA that is in the transcription regulatory region for RNA polymerase II (RNAP II) in order to activate or increase the frequency, rate or extent of transcription from the RNAP II promoter." [GOC:krc]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0001882', 'nucleoside binding', '"Interacting selectively and non-covalently with a nucleoside, a compound consisting of a purine or pyrimidine nitrogenous base linked either to ribose or deoxyribose." [GOC:hjd]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0001883', 'purine nucleoside binding', '"Interacting selectively and non-covalently with a purine nucleoside, a compound consisting of a purine base linked either to ribose or deoxyribose." [GOC:hjd]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003674', 'molecular_function', '"Elemental activities, such as catalysis or binding, describing the actions of a gene product at the molecular level. A given gene product may exhibit one or more molecular functions." [GOC:go_curators]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003676', 'nucleic acid binding', '"Interacting selectively and non-covalently with any nucleic acid." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003677', 'DNA binding', '"Any molecular function by which a gene product interacts selectively and non-covalently with DNA (deoxyribonucleic acid)." [GOC:dph, GOC:jl, GOC:tb, GOC:vw]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003682', 'chromatin binding', '"Interacting selectively and non-covalently with chromatin, the network of fibers of DNA, protein, and sometimes RNA, that make up the chromosomes of the eukaryotic nucleus during interphase." [GOC:jl, ISBN:0198506732, PMID:20404130]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003690', 'double-stranded DNA binding', '"Interacting selectively and non-covalently with double-stranded DNA." [GOC:elh]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003700', 'transcription factor activity, sequence-specific DNA binding', '"Interacting selectively and non-covalently with a specific DNA sequence in order to modulate transcription. The transcription factor may or may not also interact selectively with a protein or macromolecular complex." [GOC:curators, GOC:txnOH]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003712', 'transcription cofactor activity', '"Interacting selectively and non-covalently with a regulatory transcription factor and also with the basal transcription machinery in order to modulate transcription. Cofactors generally do not bind the template nucleic acid, but rather mediate protein-protein interactions between regulatory transcription factors and the basal transcription machinery." [GOC:txnOH, PMID:10213677, PMID:16858867]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003713', 'transcription coactivator activity', '"Interacting selectively and non-covalently with a activating transcription factor and also with the basal transcription machinery in order to increase the frequency, rate or extent of transcription. Cofactors generally do not bind the template nucleic acid, but rather mediate protein-protein interactions between activating transcription factors and the basal transcription machinery." [GOC:txnOH, PMID:10213677, PMID:16858867]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003714', 'transcription corepressor activity', '"Interacting selectively and non-covalently with a repressing transcription factor and also with the basal transcription machinery in order to stop, prevent, or reduce the frequency, rate or extent of transcription. Cofactors generally do not bind the template nucleic acid, but rather mediate protein-protein interactions between repressive transcription factors and the basal transcription machinery." [GOC:txnOH, PMID:10213677, PMID:16858867]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003723', 'RNA binding', '"Interacting selectively and non-covalently with an RNA molecule or a portion thereof." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003735', 'structural constituent of ribosome', '"The action of a molecule that contributes to the structural integrity of the ribosome." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003774', 'motor activity', '"Catalysis of the generation of force resulting either in movement along a microfilament or microtubule, or in torque resulting in membrane scission, coupled to the hydrolysis of a nucleoside triphosphate." [GOC:mah, GOC:vw, ISBN:0815316194, PMID:11242086]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003777', 'microtubule motor activity', '"Catalysis of movement along a microtubule, coupled to the hydrolysis of a nucleoside triphosphate (usually ATP)." [GOC:mah, ISBN:0815316194]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003779', 'actin binding', '"Interacting selectively and non-covalently with monomeric or multimeric forms of actin, including actin filaments." [GOC:clt]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003824', 'catalytic activity', '"Catalysis of a biochemical reaction at physiological temperatures. In biologically catalyzed reactions, the reactants are known as substrates, and the catalysts are naturally occurring macromolecular substances known as enzymes. Enzymes possess specific binding sites for substrates, and are usually composed wholly or largely of protein, but RNA that has catalytic activity (ribozyme) is often also regarded as enzymatic." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0003924', 'GTPase activity', '"Catalysis of the reaction: GTP + H2O = GDP + phosphate." [ISBN:0198547684]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0004175', 'endopeptidase activity', '"Catalysis of the hydrolysis of internal, alpha-peptide bonds in a polypeptide chain." [http://merops.sanger.ac.uk/about/glossary.htm#ENDOPEPTIDASE]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0004222', 'metalloendopeptidase activity', '"Catalysis of the hydrolysis of internal, alpha-peptide bonds in a polypeptide chain by a mechanism in which water acts as a nucleophile, one or two metal ions hold the water molecule in place, and charged amino acid side chains are ligands for the metal ions." [GOC:mah, http://merops.sanger.ac.uk/about/glossary.htm#CATTYPE, http://merops.sanger.ac.uk/about/glossary.htm#ENDOPEPTIDASE]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0004252', 'serine-type endopeptidase activity', '"Catalysis of the hydrolysis of internal, alpha-peptide bonds in a polypeptide chain by a catalytic mechanism that involves a catalytic triad consisting of a serine nucleophile that is activated by a proton relay involving an acidic residue (e.g. aspartate or glutamate) and a basic residue (usually histidine)." [GOC:mah, http://merops.sanger.ac.uk/about/glossary.htm#CATTYPE, ISBN:0716720094]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0004672', 'protein kinase activity', '"Catalysis of the phosphorylation of an amino acid residue in a protein, usually according to the reaction: a protein + ATP = a phosphoprotein + ADP." [MetaCyc:PROTEIN-KINASE-RXN]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0004674', 'protein serine/threonine kinase activity', '"Catalysis of the reactions: ATP + protein serine = ADP + protein serine phosphate, and ATP + protein threonine = ADP + protein threonine phosphate." [GOC:bf]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0004842', 'ubiquitin-protein transferase activity', '"Catalysis of the transfer of ubiquitin from one protein to another via the reaction X-Ub + Y --> Y-Ub + X, where both X-Ub and Y-Ub are covalent linkages." [GOC:BioGRID, GOC:jh2, PMID:9635407]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0004871', 'signal transducer activity', '"Conveys a signal across a cell to trigger a change in cell function or state. A signal is a physical entity or change in state that is used to transfer information in order to trigger a response." [GOC:go_curators]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0004872', 'receptor activity', '"Combining with an extracellular or intracellular messenger to initiate a change in cell activity." [GOC:ceb, ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0004888', 'transmembrane signaling receptor activity', '"Combining with an extracellular or intracellular signal and transmitting the signal from one side of the membrane to the other to initiate a change in cell activity." [GOC:go_curators, Wikipedia:Transmembrane_receptor]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0004930', 'G-protein coupled receptor activity', '"Combining with an extracellular signal and transmitting the signal across the membrane by activating an associated G-protein; promotes the exchange of GDP for GTP on the alpha subunit of a heterotrimeric G-protein complex." [GOC:bf, http://www.iuphar-db.org, Wikipedia:GPCR]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005096', 'GTPase activator activity', '"Binds to and increases the activity of a GTPase, an enzyme that catalyzes the hydrolysis of GTP." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005102', 'receptor binding', '"Interacting selectively and non-covalently with one or more specific sites on a receptor molecule, a macromolecule that undergoes combination with a hormone, neurotransmitter, drug or intracellular messenger to initiate a change in cell function." [GOC:bf, GOC:ceb, ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005178', 'integrin binding', '"Interacting selectively and non-covalently with an integrin." [GOC:ceb]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005198', 'structural molecule activity', '"The action of a molecule that contributes to the structural integrity of a complex or assembly within or outside a cell." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005488', 'binding', '"The selective, non-covalent, often stoichiometric, interaction of a molecule with one or more specific sites on another molecule." [GOC:ceb, GOC:mah, ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005506', 'iron ion binding', '"Interacting selectively and non-covalently with iron (Fe) ions." [GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005509', 'calcium ion binding', '"Interacting selectively and non-covalently with calcium ions (Ca2+)." [GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005515', 'protein binding', '"Interacting selectively and non-covalently with any protein or protein complex (a complex of two or more proteins that may include other nonprotein molecules)." [GOC:go_curators]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005516', 'calmodulin binding', '"Interacting selectively and non-covalently with calmodulin, a calcium-binding protein with many roles, both in the calcium-bound and calcium-free states." [GOC:krc]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005524', 'ATP binding', '"Interacting selectively and non-covalently with ATP, adenosine 5''-triphosphate, a universally important coenzyme and enzyme regulator." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0005525', 'GTP binding', '"Interacting selectively and non-covalently with GTP, guanosine triphosphate." [GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0008017', 'microtubule binding', '"Interacting selectively and non-covalently with microtubules, filaments composed of tubulin monomers." [GOC:krc]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0008022', 'protein C-terminus binding', '"Interacting selectively and non-covalently with a protein C-terminus, the end of any peptide chain at which the 1-carboxy function of a constituent amino acid is not attached in peptide linkage to another amino-acid residue." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0008047', 'enzyme activator activity', '"Binds to and increases the activity of an enzyme." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0008092', 'cytoskeletal protein binding', '"Interacting selectively and non-covalently with any protein component of any cytoskeleton (actin, microtubule, or intermediate filament cytoskeleton)." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0008134', 'transcription factor binding', '"Interacting selectively and non-covalently with a transcription factor, any protein required to initiate or regulate transcription." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0008233', 'peptidase activity', '"Catalysis of the hydrolysis of a peptide bond. A peptide bond is a covalent bond formed when the carbon atom from the carboxyl group of one amino acid shares electrons with the nitrogen atom from the amino group of a second amino acid." [GOC:jl, ISBN:0815332181]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0008236', 'serine-type peptidase activity', '"Catalysis of the hydrolysis of peptide bonds in a polypeptide chain by a catalytic mechanism that involves a catalytic triad consisting of a serine nucleophile that is activated by a proton relay involving an acidic residue (e.g. aspartate or glutamate) and a basic residue (usually histidine)." [http://merops.sanger.ac.uk/about/glossary.htm#CATTYPE, ISBN:0716720094]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0008237', 'metallopeptidase activity', '"Catalysis of the hydrolysis of peptide bonds by a mechanism in which water acts as a nucleophile, one or two metal ions hold the water molecule in place, and charged amino acid side chains are ligands for the metal ions." [GOC:mah, http://merops.sanger.ac.uk/about/glossary.htm#CATTYPE]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0008270', 'zinc ion binding', '"Interacting selectively and non-covalently with zinc (Zn) ions." [GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0015631', 'tubulin binding', '"Interacting selectively and non-covalently with monomeric or multimeric forms of tubulin, including microtubules." [GOC:clt]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0016301', 'kinase activity', '"Catalysis of the transfer of a phosphate group, usually from ATP, to a substrate molecule." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0016462', 'pyrophosphatase activity', '"Catalysis of the hydrolysis of a pyrophosphate bond between two phosphate groups, leaving one phosphate on each of the two fragments." [EC:3.6.1.-, GOC:curators]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0016740', 'transferase activity', '"Catalysis of the transfer of a group, e.g. a methyl group, glycosyl group, acyl group, phosphorus-containing, or other groups, from one compound (generally regarded as the donor) to another compound (generally regarded as the acceptor). Transferase is the systematic name for any enzyme of EC class 2." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0016772', 'transferase activity, transferring phosphorus-containing groups', '"Catalysis of the transfer of a phosphorus-containing group from one compound (donor) to another (acceptor)." [GOC:jl, ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0016773', 'phosphotransferase activity, alcohol group as acceptor', '"Catalysis of the transfer of a phosphorus-containing group from one compound (donor) to an alcohol group (acceptor)." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0016787', 'hydrolase activity', '"Catalysis of the hydrolysis of various bonds, e.g. C-O, C-N, C-C, phosphoric anhydride bonds, etc. Hydrolase is the systematic name for any enzyme of EC class 3." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0016817', 'hydrolase activity, acting on acid anhydrides', '"Catalysis of the hydrolysis of any acid anhydride." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0016818', 'hydrolase activity, acting on acid anhydrides, in phosphorus-containing anhydrides', '"Catalysis of the hydrolysis of any acid anhydride which contains phosphorus." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0016874', 'ligase activity', '"Catalysis of the joining of two substances, or two groups within a single molecule, with the concomitant hydrolysis of the diphosphate bond in ATP or a similar triphosphate." [EC:6, GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0017076', 'purine nucleotide binding', '"Interacting selectively and non-covalently with purine nucleotides, any compound consisting of a purine nucleoside esterified with (ortho)phosphate." [GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0017111', 'nucleoside-triphosphatase activity', '"Catalysis of the reaction: a nucleoside triphosphate + H2O = nucleoside diphosphate + phosphate." [EC:3.6.1.15]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0017171', 'serine hydrolase activity', '"Catalysis of the hydrolysis of a substrate by a catalytic mechanism that involves a catalytic triad consisting of a serine nucleophile that is activated by a proton relay involving an acidic residue (e.g. aspartate or glutamate) and a basic residue (usually histidine)." [Wikipedia:Serine_hydrolase]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0019001', 'guanyl nucleotide binding', '"Interacting selectively and non-covalently with guanyl nucleotides, any compound consisting of guanosine esterified with (ortho)phosphate." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0019787', 'ubiquitin-like protein transferase activity', '"Catalysis of the transfer of a ubiquitin-like from one protein to another via the reaction X-ULP + Y --> Y-ULP + X, where both X-ULP and Y-ULP are covalent linkages. ULP represents a ubiquitin-like protein." [GOC:mah, GOC:rn, PMID:10806345, PMID:10884686]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0019899', 'enzyme binding', '"Interacting selectively and non-covalently with any enzyme." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0019900', 'kinase binding', '"Interacting selectively and non-covalently with a kinase, any enzyme that catalyzes the transfer of a phosphate group." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0019901', 'protein kinase binding', '"Interacting selectively and non-covalently with a protein kinase, any enzyme that catalyzes the transfer of a phosphate group, usually from ATP, to a protein substrate." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0020037', 'heme binding', '"Interacting selectively and non-covalently with heme, any compound of iron complexed in a porphyrin (tetrapyrrole) ring." [CHEBI:30413, GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0030234', 'enzyme regulator activity', '"Binds to and modulates the activity of an enzyme." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0030554', 'adenyl nucleotide binding', '"Interacting selectively and non-covalently with adenyl nucleotides, any compound consisting of adenosine esterified with (ortho)phosphate." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0030695', 'GTPase regulator activity', '"Modulates the rate of GTP hydrolysis by a GTPase." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0031625', 'ubiquitin protein ligase binding', '"Interacting selectively and non-covalently with a ubiquitin protein ligase enzyme, any of the E3 proteins." [GOC:vp]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0032403', 'protein complex binding', '"Interacting selectively and non-covalently with any protein complex (a complex of two or more proteins that may include other nonprotein molecules)." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0032549', 'ribonucleoside binding', '"Interacting selectively and non-covalently with a ribonucleoside, a compound consisting of a purine or pyrimidine nitrogenous base linked to ribose." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0032550', 'purine ribonucleoside binding', '"Interacting selectively and non-covalently with a purine ribonucleoside, a compound consisting of a purine base linked to ribose." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0032553', 'ribonucleotide binding', '"Interacting selectively and non-covalently with a ribonucleotide, any compound consisting of a ribonucleoside that is esterified with (ortho)phosphate or an oligophosphate at any hydroxyl group on the ribose moiety." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0032555', 'purine ribonucleotide binding', '"Interacting selectively and non-covalently with a purine ribonucleotide, any compound consisting of a purine ribonucleoside that is esterified with (ortho)phosphate or an oligophosphate at any hydroxyl group on the ribose moiety." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0032559', 'adenyl ribonucleotide binding', '"Interacting selectively and non-covalently with an adenyl ribonucleotide, any compound consisting of adenosine esterified with (ortho)phosphate or an oligophosphate at any hydroxyl group on the ribose moiety." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0032561', 'guanyl ribonucleotide binding', '"Interacting selectively and non-covalently with a guanyl ribonucleotide, any compound consisting of guanosine esterified with (ortho)phosphate or an oligophosphate at any hydroxyl group on the ribose moiety." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0035639', 'purine ribonucleoside triphosphate binding', '"Interacting selectively and non-covalently with a purine ribonucleoside triphosphate, a compound consisting of a purine base linked to a ribose sugar esterified with triphosphate on the sugar." [CHEBI:26398, GOC:BHF, GOC:ebc, ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0036094', 'small molecule binding', '"Interacting selectively and non-covalently with a small molecule, any low molecular weight, monomeric, non-encoded molecule." [GOC:curators, GOC:pde, GOC:pm]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0038023', 'signaling receptor activity', '"Receiving a signal and transmitting the signal in the cell to initiate a change in cell activity. A signal is a physical entity or change in state that is used to transfer information in order to trigger a response." [GOC:bf, GOC:signaling]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0042802', 'identical protein binding', '"Interacting selectively and non-covalently with an identical protein or proteins." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0042803', 'protein homodimerization activity', '"Interacting selectively and non-covalently with an identical protein to form a homodimer." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0043167', 'ion binding', '"Interacting selectively and non-covalently with ions, charged atoms or groups of atoms." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0043168', 'anion binding', '"Interacting selectively and non-covalently with anions, charged atoms or groups of atoms with a net negative charge." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0043169', 'cation binding', '"Interacting selectively and non-covalently with cations, charged atoms or groups of atoms with a net positive charge." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0043565', 'sequence-specific DNA binding', '"Interacting selectively and non-covalently with DNA of a specific nucleotide composition, e.g. GC-rich DNA binding, or with a specific sequence motif or type of DNA e.g. promotor binding or rDNA binding." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0043566', 'structure-specific DNA binding', '"Interacting selectively and non-covalently with DNA of a specific structure or configuration e.g. triplex DNA binding or bent DNA binding." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0044212', 'transcription regulatory region DNA binding', '"Interacting selectively and non-covalently with a DNA region that regulates the transcription of a region of DNA, which may be a gene, cistron, or operon. Binding may occur as a sequence specific interaction or as an interaction observed only once a factor has been recruited to the DNA by other factors." [GOC:jl, GOC:txnOH, SO:0005836]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0044389', 'ubiquitin-like protein ligase binding', '"Interacting selectively and non-covalently with a ubiquitin-like protein ligase, such as ubiquitin-ligase." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0044877', 'macromolecular complex binding', '"Interacting selectively and non-covalently with any macromolecular complex." [GOC:jl]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0046872', 'metal ion binding', '"Interacting selectively and non-covalently with any metal ion." [GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0046906', 'tetrapyrrole binding', '"Interacting selectively and non-covalently with a tetrapyrrole, a compound containing four pyrrole nuclei variously substituted and linked to each other through carbons at the alpha position." [CHEBI:26932, GOC:curators, ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0046914', 'transition metal ion binding', '"Interacting selectively and non-covalently with a transition metal ions; a transition metal is an element whose atom has an incomplete d-subshell of extranuclear electrons, or which gives rise to a cation or cations with an incomplete d-subshell. Transition metals often have more than one valency state. Biologically relevant transition metals include vanadium, manganese, iron, copper, cobalt, nickel, molybdenum and silver." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0046982', 'protein heterodimerization activity', '"Interacting selectively and non-covalently with a nonidentical protein to form a heterodimer." [GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0046983', 'protein dimerization activity', '"The formation of a protein dimer, a macromolecular structure consists of two noncovalently associated identical or nonidentical subunits." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0047485', 'protein N-terminus binding', '"Interacting selectively and non-covalently with a protein N-terminus, the end of any peptide chain at which the 2-amino (or 2-imino) function of a constituent amino acid is not attached in peptide linkage to another amino-acid residue." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0048037', 'cofactor binding', '"Interacting selectively and non-covalently with a cofactor, a substance that is required for the activity of an enzyme or other protein. Cofactors may be inorganic, such as the metal atoms zinc, iron, and copper in certain forms, or organic, in which case they are referred to as coenzymes. Cofactors may either be bound tightly to active sites or bind loosely with the substrate." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0050660', 'flavin adenine dinucleotide binding', '"Interacting selectively and non-covalently with FAD, flavin-adenine dinucleotide, the coenzyme or the prosthetic group of various flavoprotein oxidoreductase enzymes, in either the oxidized form, FAD, or the reduced form, FADH2." [CHEBI:24040, GOC:ai, GOC:imk, ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0050662', 'coenzyme binding', '"Interacting selectively and non-covalently with a coenzyme, any of various nonprotein organic cofactors that are required, in addition to an enzyme and a substrate, for an enzymatic reaction to proceed." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0050839', 'cell adhesion molecule binding', '"Interacting selectively and non-covalently with a cell adhesion molecule." [GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0051015', 'actin filament binding', '"Interacting selectively and non-covalently with an actin filament, also known as F-actin, a helical filamentous polymer of globular G-actin subunits." [ISBN:0198506732]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0051082', 'unfolded protein binding', '"Interacting selectively and non-covalently with an unfolded protein." [GOC:ai]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0060089', 'molecular transducer activity', '"The molecular function that accepts an input of one form and creates an output of a different form." [GOC:mtg_MIT_16mar07]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0060589', 'nucleoside-triphosphatase regulator activity', '"Modulates the rate of NTP hydrolysis by a NTPase." [GOC:dph, GOC:tb]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0070011', 'peptidase activity, acting on L-amino acid peptides', '"Catalysis of the hydrolysis of peptide bonds formed between L-amino acids." [GOC:mah]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0097159', 'organic cyclic compound binding', '"Interacting selectively and non-covalently with an organic cyclic compound, any molecular entity that contains carbon arranged in a cyclic molecular structure." [CHEBI:33832, GOC:sjw, PMID:7583672]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0097367', 'carbohydrate derivative binding', '"Interacting selectively and non-covalently with a carbohydrate derivative." [GOC:pr]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:0098772', 'molecular function regulator', '"A molecular function that modulates the activity of a gene product or complex.  Examples include enzyme regulators and channel regulators." [GOC:dos, GOC:pt]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:1901265', 'nucleoside phosphate binding', '"Interacting selectively and non-covalently with nucleoside phosphate." [GOC:TermGenie]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:1901363', 'heterocyclic compound binding', '"Interacting selectively and non-covalently with heterocyclic compound." [GOC:TermGenie]');
INSERT INTO public.go_molecular_function (go_id, name, definition) VALUES ('GO:1990837', 'sequence-specific double-stranded DNA binding', '"Interacting selectively and non-covalently with double-stranded DNA of a specific nucleotide composition, e.g. GC-rich DNA binding, or with a specific sequence motif or type of DNA, e.g. promotor binding or rDNA binding." [GOC:dos, GOC:sl]');
